var util = require('util'),
    colors = require('colors'),
    http = require('http'),
    https = require('https'),
    httpProxy = require('../../lib/node-http-proxy');


var url = require('url');

var fs = require('fs'),
    path = require('path');

var fixturesDir = path.join(__dirname, '../../test/.', 'fixtures');

//
// @https {Object}
// Returns the necessary `https` credentials.
//
var httpsConfig = {
            key:  fs.readFileSync(path.join(fixturesDir, 'agent2-key.pem'), 'utf8'),
            cert: fs.readFileSync(path.join(fixturesDir, 'agent2-cert.pem'), 'utf8')
        };


var welcome = [
  '#    # ##### ##### #####        #####  #####   ####  #    # #   #',
  '#    #   #     #   #    #       #    # #    # #    #  #  #   # # ',  
  '######   #     #   #    # ##### #    # #    # #    #   ##     #  ',   
  '#    #   #     #   #####        #####  #####  #    #   ##     #  ',   
  '#    #   #     #   #            #      #   #  #    #  #  #    #  ',   
  '#    #   #     #   #            #      #    #  ####  #    #   #  '
].join('\n');

util.puts(welcome.rainbow.bold);



//
// Create a proxy server with custom application logic
//
var server = httpProxy.createServer(function (req, res, proxy) {
    var request_url = url.parse(req.url);
  //  console.log(req.method, req.url)
    var proxy_options = {};

    proxy_options.host = request_url.hostname;
    proxy_options.port = request_url.port || 80;



    //
    // Put your custom server logic here
    //
    proxy.proxyRequest(req, res,  proxy_options);


}).listen(8000);

// Listen for the `proxyError` event on `server.proxy`. _It will not
// be raised on the server itself._
server.proxy.on('proxyError', function (err, req, res) {
    res.writeHead(500, {
    });
    res.end();
});



//
// Create the target HTTPS server
//
//

https.createServer(httpsConfig,function(req,res){
    res.writeHead(500, {
    });

    console.log(123);
    res.write("123123")
    res.end();
}).listen(8083)

util.puts('http proxy server'.blue + ' started '.green.bold + 'on port '.blue + '8000'.yellow);

